mapper.select:
select id,username,password,name,phone,head_url,post_id, dept_id,description,status,create_time,update_time,is_deleted from sys_user
and (username like CONCAT('%',#{vo.keyword},'%') or name like CONCAT('%',#{vo.keyword},'%') or phone like CONCAT('%',#{vo.keyword},'%'))
and create_time >= #{vo.createTimeBegin}
and create_time <= #{vo.createTimeEnd}
and is_deleted=0
order by id desc
